mapper.update:
UPDATE TB_ECOM_GOODS_CATEGORY
CAT_NAME=#{catName,jdbcType=VARCHAR}, PARENT_ID=#{parentId,jdbcType=VARCHAR}, CAT_PATH=#{catPath,jdbcType=VARCHAR}, CAT_SOLR=#{catSolr,jdbcType=DOUBLE}, LIST_SHOW=#{listShow,jdbcType=VARCHAR}, CAT_IMAGE=#{catImage,jdbcType=VARCHAR}, OUTER_CAT_ID=#{outerCatId,jdbcType=VARCHAR}, ECOM_CODE=#{ecomCode,jdbcType=VARCHAR}, REMARKS=#{remarks,jdbcType=VARCHAR}, DATA_STAT=#{dataStat,jdbcType=VARCHAR}, UPDATE_USER=#{updateUser,jdbcType=VARCHAR}, UPDATE_TIME=SYSDATE, LOCK_VERSION=LOCK_VERSION+1,
where CAT_ID=#{catId}

mapper.select:
SELECT
	 
FROM TB_ECOM_GOODS_CATEGORY
 WHERE DATA_STAT='0'
 AND CAT_NAME=#{catName,jdbcType=VARCHAR}
 AND CAT_PATH=#{catPath,jdbcType=VARCHAR}
 AND ECOM_CODE=#{ecomCode,jdbcType=VARCHAR}
 AND PARENT_ID=#{parentId,jdbcType=VARCHAR}

SELECT
	 
FROM TB_ECOM_GOODS_CATEGORY
 WHERE DATA_STAT='0'
 AND OUTER_CAT_ID=#{outerCatId,jdbcType=VARCHAR}
 AND CAT_PATH=#{catPath,jdbcType=VARCHAR}
 AND ECOM_CODE=#{ecomCode,jdbcType=VARCHAR}
 AND PARENT_ID=#{parentId,jdbcType=VARCHAR}

		SELECT 
		
	  FROM TB_ECOM_GOODS_CATEGORY C
		 WHERE C.DATA_STAT = '0'
		   AND C.CAT_PATH = #{catPath}
		   AND C.PARENT_ID = #{parentId}
		   AND C.ECOM_CODE = #{ecomCode}
		   
		   AND C.CAT_NAME LIKE '%' || #{catName} || '%'
		   
		   
   		   AND C.LIST_SHOW = #{listShow}
   		   
   		   ORDER BY C.CAT_SOLR

SELECT C1.CAT_ID, C1.CAT_NAME, C1.PARENT_ID, C1.CAT_PATH, C1.CAT_SOLR, C1.LIST_SHOW, C1.CAT_IMAGE, C1.ECOM_CODE, C1.DATA_STAT, C1.REMARKS, C1.CREATE_USER, C1.UPDATE_USER, C1.CREATE_TIME, C1.UPDATE_TIME, C1.LOCK_VERSION, C2.CAT_NAME AS PARENT_NAME FROM TB_ECOM_GOODS_CATEGORY C1, TB_ECOM_GOODS_CATEGORY C2 WHERE C1.PARENT_ID = C2.CAT_ID AND C1.DATA_STAT = '0' AND C2.DATA_STAT = '0' AND C1.CAT_PATH = '2' AND C1.ECOM_CODE = #{ecomCode}
AND C1.CAT_NAME LIKE '%' || #{catName} || '%'
AND C1.LIST_SHOW = #{listShow}
AND C2.CAT_NAME LIKE '%' || #{parentName} || '%'

SELECT C1.CAT_ID, C1.ECOM_CODE, C1.CAT_NAME, C1.CAT_PATH, C1.CAT_IMAGE, C1.LIST_SHOW, C1.CAT_SOLR, C2.CAT_NAME AS PARENT_NAME, C1.OUTER_CAT_ID, C1.CREATE_TIME, C1.UPDATE_TIME FROM TB_ECOM_GOODS_CATEGORY C1 LEFT JOIN TB_ECOM_GOODS_CATEGORY C2 ON C1.PARENT_ID = C2.CAT_ID WHERE C1.DATA_STAT = '0' AND C1.ECOM_CODE = #{ecomCode}
AND C1.CAT_NAME LIKE '%' || #{catName} || '%'
AND C1.LIST_SHOW = #{listShow}
AND C2.CAT_NAME LIKE '%' || #{parentName} || '%'
