mapper.select:
	SELECT 
  		 NVL(SUM(ACC_BAL),'0') as  accBal
			FROM TB_MCHNT_ACCT_INF m where 1=1

					AND m.account_type=#{acctType}	 	  		


					AND m.account_stat=#{acctSata}	 	  		


					AND m.INS_ID=#{insId}	 	  		


					AND m.mancht_id=#{manchtId}

SELECT NVL(SUM(CB.CARD_BAL),0) as cardBal FROM TB_CARD_BAL cb, TB_PRODUCT p, TB_REL_INS_PRODUCT rif , TB_INS_INF ii, TB_MERCHANT_INF mi WHERE cb.PRODUCT_CODE=p.PRODUCT_CODE and cb.ins_code=ii.ins_code and p.PRODUCT_CODE=rif.PRODUCT_CODE and rif.INS_ID=ii.INS_ID and ii.INS_ID=mi.INS_ID
and cb.SETTLE_DATE=#{settleDate}
and mi.ins_id=#{insId}
and mi.mchnt_id=#{manchtId}
