mapper.select:
SELECT TRANS_RULE_ID, INS_ID, PLATFORM_ID, MCHNT_ID, SHOP_ID, START_TIME, END_TIME, IS_SUPERPOSED, DATA_STAT, SALIENCE, TEMPLATE_CODE, TEMPLATE_NAME, TEMPLATE_DESP, TEMPLATE_FROM, RULE_TYPE_CODE, RULE_NAME, RULE_TYPE, RULE_ID, RULE_FACTOR_NUM, RULE_FACTOR_ID, RULE_FACTOR, RULE_PARAM FROM VIEW_TRANS_RULE_DECLARE vt
WHERE vt.DATA_STAT='0'
AND vt.TEMPLATE_CODE＝#{templateCode}
AND vt.INS_ID＝#{insId}
AND vt.PLATFORM_ID＝#{platformId}
AND vt.MCHNT_ID＝#{mchntId}
AND vt.SHOP_ID＝#{shopId}

		SELECT 
		 	count(RULE_ID)
		 FROM VIEW_TRANS_RULE_DECLARE vt
              LEFT JOIN TB_MERCHANT_INF mi on  mi.mchnt_id=vt.MCHNT_ID
              LEFT JOIN  TB_SHOP_INF si on si.SHOP_ID =vt.SHOP_ID
		         WHERE vt.DATA_STAT='0'
		         	AND　  vt.END_TIME>sysdate

	  					AND vt.INS_ID＝#{entity.insId}


	  					AND vt.PLATFORM_ID＝#{entity.platformId}


	  					 AND vt.MCHNT_ID＝#{entity.mchntId}


	  					 AND vt.SHOP_ID＝#{entity.shopId}

SELECT * FROM ( SELECT a.*, ROWNUM rn FROM ( SELECT vt.RULE_ID, vt.MCHNT_ID, mi.MCHNT_NAME, vt.SHOP_ID, si.SHOP_NAME, vt.TRANS_RULE_DESP, vt.START_TIME, vt.END_TIME FROM VIEW_TRANS_RULE_DECLARE vt LEFT JOIN TB_MERCHANT_INF mi on mi.mchnt_id=vt.MCHNT_ID LEFT JOIN TB_SHOP_INF si on si.SHOP_ID =vt.SHOP_ID WHERE vt.DATA_STAT='0' AND vt.END_TIME>sysdate
AND vt.INS_ID＝#{entity.insId}
AND vt.PLATFORM_ID＝#{entity.platformId}
AND vt.MCHNT_ID＝#{entity.mchntId}
AND vt.SHOP_ID＝#{entity.shopId}
ORDER BY vt.END_TIME DESC ) a WHERE ROWNUM <= (#{page.pageNum}* #{page.pageSize}) ) b WHERE rn >= (((#{page.pageNum}-1)* #{page.pageSize})+1)
