mapper.select:
SELECT T.MCHNT_CODE, CARD_PAY_AMT, QUICK_PAY_AMT, OL_RECHARGE_DEN, OL_RECHARGE_AMT, PF_RECHARGE_DEN, PF_RECHARGE_AMT, PF_SUBSIDY_NUM, PF_SUBSIDY_AMT, CARD_BAL, FEE, SETTLE_AMT FROM (SELECT S.MCHNT_CODE, SUM(S.CARD_PAY_AMT) AS CARD_PAY_AMT, SUM(S.QUICK_PAY_AMT) AS QUICK_PAY_AMT, SUM(S.OL_RECHARGE_DEN) AS OL_RECHARGE_DEN, SUM(S.OL_RECHARGE_AMT) AS OL_RECHARGE_AMT, SUM(S.PF_RECHARGE_DEN) AS PF_RECHARGE_DEN, SUM(S.PF_RECHARGE_AMT) AS PF_RECHARGE_AMT, SUM(S.PF_SUBSIDY_NUM) AS PF_SUBSIDY_NUM, SUM(S.PF_SUBSIDY_AMT) AS PF_SUBSIDY_AMT, SUM(S.CARD_BAL) AS CARD_BAL, SUM(S.FEE) AS FEE, SUM(S.SETTLE_AMT) AS SETTLE_AMT FROM TB_MCHNT_STATISTICS S WHERE S.DATA_STAT = '0' AND S.MCHNT_CODE = #{mchntCode}
AND S.SETTLE_DATE >= #{startDate}
AND S.SETTLE_DATE <= #{endDate}
GROUP BY S.MCHNT_CODE) T, TB_MERCHANT_INF M WHERE T.MCHNT_CODE = M.MCHNT_CODE

    SELECT M.CARD_BAL
	  FROM TB_MCHNT_STATISTICS M
	 WHERE M.SETTLE_DATE = #{endDate} 
	   AND M.MCHNT_CODE = #{mchntCode}
