mapper.select:
SELECT S.SHOP_NAME, PAY_AMT FROM (SELECT POS.SHOP_CODE, NVL(SUM(POS.PAY_AMT), 0) AS PAY_AMT FROM TB_POSIT_OPR_STATISTICS POS, TB_SHOP_OPR_STATISTICS SOS WHERE POS.SHOP_STATISTICS_OPR_ID = SOS.ID AND POS.DATA_STAT = '0' AND POS.STAT IN ('1', '2') AND SOS.SHOP_CODE = #{shopCode}
AND POS.SETTLE_DATE >= #{startDate}
AND POS.SETTLE_DATE <= #{endDate}
GROUP BY POS.SHOP_CODE) T, TB_SHOP_INF S WHERE S.SHOP_CODE = T.SHOP_CODE

 
        SELECT SUM(SOS.REMARKS) AS TOTAL_PAY
         FROM TB_SHOP_OPR_STATISTICS SOS
        WHERE SOS.DATA_STAT = '0'
          AND SOS.SHOP_CODE = #{shopCode}
          AND SOS.STAT IN ('1', '2')
        
			AND SOS.SETTLE_DATE >= #{startDate}
		
		
			AND SOS.SETTLE_DATE <= #{endDate}
		
        GROUP BY SOS.SHOP_CODE

SELECT SHOP_NAME FROM TB_SHOP_INF
WHERE DATA_STAT='0' AND
SHOP_CODE = #{shopCode}

SELECT SUBSTR(SS.SETTLE_DATE,0,6) AS SETTLE_DATE, SUM(SS.CARD_PAY_AMT+SS.QUICK_PAY_AMT) AS PAY_AMT, SUM(NVL(SOS.REMARKS,SS.CARD_PAY_AMT+SS.QUICK_PAY_AMT)) AS U_PAY_AMT FROM TB_SHOP_STATISTICS SS LEFT JOIN TB_SHOP_OPR_STATISTICS SOS ON SOS.SHOP_CODE = SS.SHOP_CODE AND SOS.SETTLE_DATE = SS.SETTLE_DATE WHERE SS.DATA_STAT='0' AND SS.SHOP_CODE = #{shopCode}
AND SOS.STAT = #{stat}
AND SUBSTR(SS.SETTLE_DATE,0,6) >= SUBSTR(#{startDate},0,6)
AND SUBSTR(SS.SETTLE_DATE,0,6) <= SUBSTR(#{endDate},0,6)
GROUP BY SUBSTR(SS.SETTLE_DATE,0,6) ORDER BY SETTLE_DATE DESC

SELECT SOS.ID, SOS.MCHNT_STATISTICS_OPR_ID, SOS.SHOP_CODE, SS.SETTLE_DATE, SS.CARD_PAY_AMT+SS.QUICK_PAY_AMT AS PAY_AMT, NVL(SOS.REMARKS,SS.CARD_PAY_AMT+SS.QUICK_PAY_AMT) AS U_PAY_AMT, STAT FROM TB_SHOP_STATISTICS SS LEFT JOIN TB_SHOP_OPR_STATISTICS SOS ON SOS.SHOP_CODE = SS.SHOP_CODE AND SOS.SETTLE_DATE = SS.SETTLE_DATE WHERE SS.DATA_STAT='0' AND SS.SHOP_CODE = #{shopCode}
AND SOS.STAT = #{stat}
AND SUBSTR(SS.SETTLE_DATE,0,6) >= SUBSTR(#{startDate},0,6)
AND SUBSTR(SS.SETTLE_DATE,0,6) <= SUBSTR(#{endDate},0,6)
ORDER BY SS.SETTLE_DATE DESC

SELECT S.SHOP_NAME AS SHOP_NAME, POS.ID, POS.SHOP_STATISTICS_OPR_ID, PS.SHOP_CODE, PS.SETTLE_DATE, PS.PAY_AMT, NVL(POS.PAY_AMT, PS.PAY_AMT) AS U_PAY_AMT, POS.STAT FROM TB_SHOP_INF S LEFT JOIN TB_POSIT_STATISTICS PS ON S.SHOP_CODE = PS.SHOP_CODE LEFT JOIN TB_POSIT_OPR_STATISTICS POS ON POS.SHOP_CODE = PS.SHOP_CODE AND POS.SETTLE_DATE =PS.SETTLE_DATE WHERE S.P_SHOP_CODE = #{shopCode} AND PS.DATA_STAT = '0' AND S.DATA_STAT = '0'
AND PS.SETTLE_DATE = #{settleDate}
ORDER BY PS.SETTLE_DATE DESC

SELECT S.SHOP_NAME, PAY_AMT FROM (SELECT P.SHOP_CODE, NVL(SUM(P.PAY_AMT), 0) AS PAY_AMT FROM TB_POSIT_OPR_STATISTICS P, TB_SHOP_OPR_STATISTICS S WHERE P.DATA_STAT = '0' AND P.SHOP_STATISTICS_OPR_ID = S.ID AND S.STAT IN ('1','2') AND S.SHOP_CODE = #{shopCode}
AND P.SETTLE_DATE >= #{startDate}
AND P.SETTLE_DATE <= #{endDate}
GROUP BY P.SHOP_CODE) T, TB_SHOP_INF S WHERE T.SHOP_CODE = S.SHOP_CODE
