mapper.select:
SELECT S.SHOP_NAME, T.SHOP_CODE, CARD_PAY_AMT, CARD_PAY_NUM, QUICK_PAY_AMT, QUICK_PAY_NUM, PAY_AMT FROM (SELECT P.SHOP_CODE, SUM(P.CARD_PAY_AMT) AS CARD_PAY_AMT, SUM(P.CARD_PAY_NUM) AS CARD_PAY_NUM, SUM(P.QUICK_PAY_AMT) AS QUICK_PAY_AMT, SUM(P.QUICK_PAY_NUM) AS QUICK_PAY_NUM, SUM(P.PAY_AMT) AS PAY_AMT FROM TB_POSIT_STATISTICS P, TB_SHOP_STATISTICS ST WHERE P.DATA_STAT = '0' AND P.SHOP_STATISTICS_ID = ST.ID AND ST.SHOP_CODE = #{shCode}
AND P.SETTLE_DATE >= #{startDate}
AND P.SETTLE_DATE <= #{endDate}
GROUP BY P.SHOP_CODE) T, TB_SHOP_INF S WHERE S.SHOP_CODE = T.SHOP_CODE

SELECT S.SHOP_NAME, CARD_PAY_AMT, CARD_PAY_NUM, QUICK_PAY_AMT, QUICK_PAY_NUM, PAY_AMT FROM (SELECT P.SHOP_CODE, SUM(P.CARD_PAY_AMT) AS CARD_PAY_AMT, SUM(P.CARD_PAY_NUM) AS CARD_PAY_NUM, SUM(P.QUICK_PAY_AMT) AS QUICK_PAY_AMT, SUM(P.QUICK_PAY_NUM) AS QUICK_PAY_NUM, SUM(P.PAY_AMT) AS PAY_AMT FROM TB_POSIT_STATISTICS P, TB_SHOP_STATISTICS ST WHERE P.DATA_STAT = '0' AND P.SHOP_STATISTICS_ID = ST.ID AND ST.SHOP_CODE = #{shCode}
AND P.SETTLE_DATE >= #{startDate}
AND P.SETTLE_DATE <= #{endDate}
GROUP BY P.SHOP_CODE) T, TB_SHOP_INF S WHERE T.SHOP_CODE = S.SHOP_CODE
