mapper.select:
SELECT S.SHOP_NAME, S.SHOP_CODE, CARD_PAY_AMT, CARD_PAY_NUM, QUICK_PAY_AMT, QUICK_PAY_NUM, OL_RECHARGE_AMT, OL_RECHARGE_DEN, PF_RECHARGE_AMT, PF_RECHARGE_DEN, PF_SUBSIDY_NUM, PF_SUBSIDY_AMT FROM (SELECT S.SHOP_CODE, SUM(S.CARD_PAY_AMT) AS CARD_PAY_AMT, SUM(S.CARD_PAY_NUM) AS CARD_PAY_NUM, SUM(S.QUICK_PAY_AMT) AS QUICK_PAY_AMT, SUM(S.QUICK_PAY_NUM) AS QUICK_PAY_NUM, SUM(S.OL_RECHARGE_AMT) AS OL_RECHARGE_AMT, SUM(S.OL_RECHARGE_DEN) AS OL_RECHARGE_DEN, SUM(S.PF_RECHARGE_AMT) AS PF_RECHARGE_AMT, SUM(S.PF_RECHARGE_DEN) AS PF_RECHARGE_DEN, SUM(S.PF_SUBSIDY_NUM) AS PF_SUBSIDY_NUM, SUM(S.PF_SUBSIDY_AMT) AS PF_SUBSIDY_AMT FROM TB_SHOP_STATISTICS S, TB_MCHNT_STATISTICS M WHERE S.DATA_STAT = '0' AND M.ID = S.MCHNT_STATISTICS_ID AND M.MCHNT_CODE = #{mchntCode}
AND S.SHOP_CODE IN
${item}
AND S.SETTLE_DATE >= #{startDate}
AND S.SETTLE_DATE <= #{endDate}
GROUP BY S.SHOP_CODE) T, TB_SHOP_INF S WHERE S.SHOP_CODE = T.SHOP_CODE

SELECT S.SHOP_NAME, CARD_PAY_AMT, QUICK_PAY_AMT, PF_SUBSIDY_NUM, PF_SUBSIDY_AMT FROM (SELECT S.SHOP_CODE, SUM(S.CARD_PAY_AMT) AS CARD_PAY_AMT, SUM(S.CARD_PAY_NUM) AS CARD_PAY_NUM, SUM(S.QUICK_PAY_AMT) AS QUICK_PAY_AMT, SUM(S.QUICK_PAY_NUM) AS QUICK_PAY_NUM, SUM(S.PF_SUBSIDY_NUM) AS PF_SUBSIDY_NUM, SUM(S.PF_SUBSIDY_AMT) AS PF_SUBSIDY_AMT FROM TB_SHOP_STATISTICS S, TB_MCHNT_STATISTICS M WHERE S.DATA_STAT = '0' AND M.ID = S.MCHNT_STATISTICS_ID AND M.MCHNT_CODE = #{mchntCode}
AND S.SHOP_CODE IN
${item}
AND S.SETTLE_DATE >= #{startDate}
AND S.SETTLE_DATE <= #{endDate}
GROUP BY S.SHOP_CODE) T, TB_SHOP_INF S WHERE S.SHOP_CODE = T.SHOP_CODE
