mapper.select:
		select 
			,
			e.ROLE_NAME,e.SEQ 
		from TB_WEB_DATA_AUTH c,TB_WEB_USER_DATA_AUTH b,TB_WEB_USER a, TB_WEB_USER_ROLE d, TB_WEB_ROLE e 
    	where c.ID = b.AUTH_ID and b.USER_ID = a.ID and a.ID = d.USER_ID and d.ROLE_ID = e.ID 
		 and a.DATA_STAT = '0' 
		 
		 	and c.MCHNT_CODE = #{mchntCode}
		 
		
		 	and a.PHONE_NO = #{phoneNo}
		
		
		 	and e.SEQ >=  #{seq}
		
		order by e.SEQ asc

mapper.update:
update TB_WEB_USER a
a.LOGIN_NAME = #{loginName,jdbcType=VARCHAR}, a.PASSWORD = #{password,jdbcType=VARCHAR}, a.USER_NAME = #{userName,jdbcType=VARCHAR}, a.PHONE_NO = #{phoneNo,jdbcType=VARCHAR}, a.ISDEFAULT = #{isdefault,jdbcType=CHAR}, a.DATA_STAT = #{dataStat,jdbcType=CHAR}, a.REMARKS = #{remarks,jdbcType=VARCHAR}, a.UPDATE_USER = #{updateUser,jdbcType=VARCHAR}, a.UPDATE_TIME=sysdate, a.LOCK_VERSION = a.LOCK_VERSION +1
where a.ID = #{id}
