mapper.sql:
( SELECT m.MCHNT_CODE, m.MCHNT_NAME, m.INDUSTRY_TYPE1 as INDUSTRY_TYPE, m.SOLD_COUNT, s.SHOP_CODE, s.SHOP_NAME, s.SHOP_ADDR, s.EVALUATE, s.LATITUDE, s.LONGITUDE, a.ACTIVE_EXPLAIN, GetDistance(#{latitude}, #{longitude}, s.LATITUDE, s.LONGITUDE) as DISTANCE FROM TB_SHOP_INF s, TB_MERCHANT_INF m,TB_MERCHANT_ACTIVE_INF a WHERE m.DATA_STAT = '0' AND s.DATA_STAT = '0' AND a.DATA_STAT='0' AND a.ACTIVE_STAT='1' AND S.SELL_CARD_FLAG = '0' AND SYSDATE BETWEEN a.START_TIME AND a.END_TIME AND s.MCHNT_ID = m.MCHNT_ID and a.MERCHANT_ID=m.MCHNT_ID
AND m.INDUSTRY_TYPE1=#{industryType}
AND substr(s.geohash,0,#{geoLength}) in
#{geohash}
)
