mapper.select:
SELECT T.DMS_RELATED_KEY AS DMS_RELATED_KEY, I.DMS_RELATED_KEY AS ORG_DMS_RELATED_KEY, SUBSTR(T.SETTLE_DATE, 1, 4) || '-' || SUBSTR(T.SETTLE_DATE, 5, 2) || '-' || SUBSTR(T.SETTLE_DATE, 7, 2) AS SETTLE_DATE, T.CARD_NO AS CARD_NO, T.PRI_ACCT_NO AS PRI_ACCT_NO, DECODE(T.TRANS_ID, 'W10', '会员卡消费', 'W71', '快捷消费') AS TRANS_TYPE, T.TRANS_AMT AS TRANSAMT, TO_CHAR(T.CREATE_TIME, 'YYYY-MM-DD HH24:MI:SS') AS TRANSTIME FROM TB_TRANS_LOG_HIS T, TB_INTFACE_TRANS_LOG_HIS I, TB_SHOP_INF S1， TB_SHOP_INF S2, TB_MERCHANT_INF M WHERE T.DMS_RELATED_KEY = I.INTERFACE_PRIMARY_KEY AND T.TRANS_ID = I.TRANS_ID AND S1.SHOP_CODE = S2.P_SHOP_CODE AND T.SHOP_CODE = S2.SHOP_CODE AND T.MCHNT_CODE = M.MCHNT_CODE AND T.TRANS_ID IN ('W10', 'W71') AND T.RESP_CODE = '00' AND T.TRANS_ST = 1
AND T.SHOP_CODE = ${positCode}
AND T.MCHNT_CODE = #{mchntCode} AND S1.SHOP_CODE = #{shopCode} AND T.SETTLE_DATE >= #{startTime} AND T.SETTLE_DATE <= #{endTime} ORDER BY T.CREATE_TIME DESC

    SELECT M.CARD_BAL
	  FROM TB_MCHNT_STATISTICS M
	 WHERE M.SETTLE_DATE = #{endTime} 
	   AND M.MCHNT_CODE = #{mchntCode}

select '总计' as dms_related_key, '/' as org_dms_related_key, '/' as settle_date, '/' as card_no, '/' as pri_acct_no, sum(t.trans_amt) as transAmt, '/' as trans_type, '/' as transTime from tb_trans_log_his t, tb_intface_trans_log_his i, tb_shop_inf s1, tb_shop_inf s2, tb_merchant_inf m where t.dms_related_key = i.interface_primary_key and t.trans_id = i.trans_id and s1.shop_code = s2.p_shop_code and t.shop_code = s2.shop_code and t.mchnt_code = m.mchnt_code and t.trans_id in ('W10', 'W71') and t.resp_code = '00' and t.trans_st = 1
AND T.SHOP_CODE = ${positCode}
AND T.MCHNT_CODE = #{mchntCode} AND S1.SHOP_CODE = #{shopCode} AND T.SETTLE_DATE >= #{startTime} AND T.SETTLE_DATE <= #{endTime}
