mapper.select:
SELECT 

FROM TB_PRODUCT t where 0 = 0
		AND t.DATA_STAT = '0'

		AND t.PRODUCT_CODE=#{productCode}


		AND t.PRODUCT_NAME LIKE '%' || #{productName} || '%' 


		AND t.CARD_BIN=#{cardBin}


		AND t.BUSINESS_TYPE=#{businessType}

ORDER BY t.UPDATE_TIME DESC

mapper.update:
UPDATE TB_PRODUCT
PRODUCT_NAME =#{productName,jdbcType=VARCHAR}, ONYMOUS_STAT =#{onymousStat,jdbcType=VARCHAR}, BUSINESS_TYPE =#{businessType,jdbcType=VARCHAR}, PRODUCT_TYPE =#{productType,jdbcType=VARCHAR}, VALIDITY_PERIOD=#{validityPeriod,jdbcType=VARCHAR}, MAX_BALANCE =#{maxBalance,jdbcType=VARCHAR}, CONSUM_TIMES =#{consumTimes,jdbcType=VARCHAR}, RECHARGE_TIMES =#{rechargeTimes,jdbcType=VARCHAR}, CVV2_ERR_TIMES =#{cvv2ErrTimes,jdbcType=VARCHAR}, CARD_FACE =#{cardFace,jdbcType=VARCHAR}, DATA_STAT =#{dataStat,jdbcType=VARCHAR}, REMARKS =#{remarks,jdbcType=VARCHAR}, UPDATE_USER =#{updateUser,jdbcType=VARCHAR}, UPDATE_TIME =sysdate, LOCK_VERSION =LOCK_VERSION+1
WHERE PRODUCT_CODE=#{productCode}
