SELECT 

 		     FROM 	 m
              WHERE m.data_stat='0' AND m.CHANNEL_ORDER_ID=#{channelOrderId}

SELECT M.CHANNEL_ORDER_ID, M.CHANNEL_ID, M.OUTER_TID, M.RECHARGE_PHONE, M.RECHARGE_TYPE, M.RECHARGE_VALUE, M.ITEM_NUM, M.PAY_AMT, M.ORDER_STAT, M.NOTIFY_STAT, M.NOTIFY_FLAG, M.NOTIFY_URL, M.APP_VERSION, M.PRODUCT_ID, M.CHANNEL_RATE, M.RESV1, M.RESV2, M.RESV3, M.RESV4, M.RESV5, M.RESV6, M.DATA_STAT, M.REMARKS, M.CREATE_USER, M.UPDATE_USER, M.CREATE_TIME, M.UPDATE_TIME, M.LOCK_VERSION, C.CHANNEL_NAME FROM M LEFT JOIN TB_TEL_CHANNEL_INF C ON M.CHANNEL_ID = C.CHANNEL_ID WHERE M.DATA_STAT='0'
AND C.CHANNEL_NAME LIKE '%' || #{channelName} || '%'
AND M.CHANNEL_ORDER_ID = #{channelOrderId}
AND M.OUTER_TID = #{outerTid}
AND M.RECHARGE_TYPE = #{rechargeType}
AND M.ORDER_STAT = #{orderStat}
AND M.NOTIFY_STAT = #{notifyStat}
ORDER BY M.UPDATE_TIME DESC

SELECT 

 		     FROM 	 m
              WHERE m.data_stat='0' AND m.OUTER_TID=#{outerId} AND m.CHANNEL_ID=#{channelId}

SELECT O.CHANNEL_ORDER_ID, P.OPER_NAME, O.RECHARGE_PHONE, O.ITEM_NUM, O.RECHARGE_VALUE, O.PAY_AMT, PR.RECHARGE_STATE, O.CREATE_TIME FROM TB_TEL_CHANNEL_ORDER_INF O, TB_TEL_CHANNEL_PRODUCT_INF P, TB_TEL_CHANNEL_INF C, TB_TEL_PROVIDER_ORDER_INF PR WHERE O.PRODUCT_ID = P.PRODUCT_ID AND O.CHANNEL_ID = C.CHANNEL_ID AND O.CHANNEL_ORDER_ID = PR.CHANNEL_ORDER_ID AND O.DATA_STAT = '0' AND P.DATA_STAT = '0' AND C.DATA_STAT = '0' AND PR.DATA_STAT = '0' AND C.RESV1 = #{mchntCode}
AND O.CHANNEL_ORDER_ID = #{channelOrderId}
AND O.RECHARGE_PHONE = #{rechargePhone}
AND PR.RECHARGE_STATE = #{rechargeState}
AND O.CREATE_TIME >= TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
AND O.CREATE_TIME <= TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
ORDER BY O.CREATE_TIME DESC

SELECT O.CHANNEL_ORDER_ID, P.OPER_NAME, O.RECHARGE_PHONE, O.ITEM_NUM, O.RECHARGE_VALUE, O.PAY_AMT, PR.RECHARGE_STATE, O.CREATE_TIME FROM TB_TEL_CHANNEL_ORDER_INF O, TB_TEL_CHANNEL_PRODUCT_INF P, TB_TEL_CHANNEL_INF C, TB_TEL_PROVIDER_ORDER_INF PR WHERE O.PRODUCT_ID = P.PRODUCT_ID AND O.CHANNEL_ID = C.CHANNEL_ID AND O.CHANNEL_ORDER_ID = PR.CHANNEL_ORDER_ID AND O.DATA_STAT = '0' AND P.DATA_STAT = '0' AND C.DATA_STAT = '0' AND PR.DATA_STAT = '0' AND C.RESV1 = #{mchntCode}
AND O.CHANNEL_ORDER_ID = #{channelOrderId}
AND O.RECHARGE_PHONE = #{rechargePhone}
AND PR.RECHARGE_STATE = #{rechargeState}
AND O.CREATE_TIME >= TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
AND O.CREATE_TIME <= TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
ORDER BY O.CREATE_TIME DESC

SELECT COUNT(1) AS ORDER_COUNT, NVL(SUM(O.RECHARGE_VALUE), 0) AS ORDER_RECHARGE_AMT, NVL(SUM(O.PAY_AMT), 0) AS ORDER_PAY_AMT FROM TB_TEL_CHANNEL_ORDER_INF O, TB_TEL_CHANNEL_PRODUCT_INF P, TB_TEL_CHANNEL_INF C, TB_TEL_PROVIDER_ORDER_INF PR WHERE O.PRODUCT_ID = P.PRODUCT_ID AND O.CHANNEL_ID = C.CHANNEL_ID AND O.CHANNEL_ORDER_ID = PR.CHANNEL_ORDER_ID AND O.DATA_STAT = '0' AND P.DATA_STAT = '0' AND C.DATA_STAT = '0' AND PR.DATA_STAT = '0' AND PR.RECHARGE_STATE = '1' AND C.RESV1 = #{mchntCode}
AND O.CREATE_TIME >= TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
AND O.CREATE_TIME <= TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
